mapper.select:
select count(*)  
	 FROM Tb_guquan_zhuangtai t  where 1=1  

		and t.id=#{id}

SELECT id, guquanid, zhuagntai, inputuser, lurushijian from Tb_guquan_zhuangtai t where 1=1
and t.id=#{id}
and t.guquanid=#{guquanid}

mapper.update:
		update Tb_guquan_zhuangtai                                                                                                                                                                                                                                                                                                                                                                                                   _guquanhuangtai set 



                        guquanid=#{guquanid},



                        zhuagntai=#{zhuagntai},



                        inputuser=#{inputuser},



                        lurushijian=#{lurushijian}


		where id=#{id}
