mapper.select:
select * from ( SELECT ROW_NUMBER() over(order by lurushijian desc) as rownumber , pk_qiyeid as id, c.userid userid, c.qiyemingcheng qiyemingcheng, c.zzjgdm zzjgdm, case when tc.custmanage is not null then tc.CUSTMANAGE else c.custmanage end as custmanage, case when tc.custmanagesource is not null then tc.custmanagesource else c.custmanagesource end as custmanagesource from tb_Qiye c left join tb_cust tc on c.USERID = tc.USERID where 1=1
and c.qiyemingcheng like '%' + #{qiyemingcheng} + '%'
)t where rownumber > = #{offset} and rownumber < = #{pageSize}+#{offset}

select count(*) from ( select pk_qiyeid as id, userid, qiyemingcheng, zzjgdm FROM tb_Qiye c where 1=1
and qiyemingcheng like '%' + #{qiyemingcheng} + '%'
) a

